body:
body {
  font-family: "Segoe UI", Arial, sans-serif;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  color: #1f2937;
  margin: 0;
  padding: 2rem;
  min-height: 100vh;
}

form {
  max-width: 900px;
  margin: 0 auto;
  background: #ffffff;
  border-radius: 20px;
  box-shadow: 0 20px 40px rgba(0, 0, 0, 0.1);
  padding: 3rem 3rem;
  border: 1px solid #e5e7eb;
}

h1 {
  margin-top: 0;
  color: #1e293b;
  font-size: 2.5rem;
  text-align: center;
  margin-bottom: 1rem;
}

h2 {
  margin-top: 3rem;
  color: #3b82f6;
  border-bottom: 2px solid #3b82f6;
  padding-bottom: 0.75rem;
  font-size: 1.5rem;
  font-weight: 700;
}

label {
  display: block;
  margin-top: 2rem;
  font-weight: 600;
  color: #374151;
  font-size: 1.1rem;
}

input,
select,
textarea {
  width: 100%;
  margin-top: 0.75rem;
  padding: 1rem 1rem;
  font-size: 1rem;
  border-radius: 12px;
  border: 2px solid #d1d5db;
  background: #f9fafb;
  box-sizing: border-box;
  transition: border-color 0.2s ease, box-shadow 0.2s ease;
}

input:focus,
select:focus,
textarea:focus {
  outline: none;
  border-color: #3b82f6;
  box-shadow: 0 0 0 3px rgba(59, 130, 246, 0.1);
}

textarea {
  min-height: 150px;
  resize: vertical;
  font-family: inherit;
}

.checkbox-group label {
  display: flex;
  gap: 0.75rem;
  align-items: flex-start;
  font-weight: 500;
  margin-top: 0.5rem;
  color: #4b5563;
}

.checkbox-group input[type="checkbox"] {
  margin-top: 0.25rem;
  width: auto;
}

button {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  gap: 0.5rem;
  margin-top: 2.5rem;
  padding: 1rem 2rem;
  border-radius: 12px;
  border: none;
  background: linear-gradient(120deg, #3b82f6, #1d4ed8);
  color: #ffffff;
  font-size: 1.1rem;
  font-weight: 600;
  cursor: pointer;
  transition: transform 0.15s ease, box-shadow 0.15s ease, background 0.15s ease;
}

button:hover {
  transform: translateY(-2px);
  box-shadow: 0 12px 24px rgba(59, 130, 246, 0.3);
  background: linear-gradient(120deg, #1d4ed8, #1e40af);
}

button:disabled {
  background: #9ca3af;
  cursor: not-allowed;
  box-shadow: none;
  transform: none;
}

.hint {
  font-size: 0.95rem;
  color: #6b7280;
  margin-top: 0.5rem;
  font-style: italic;
}

.page {
  display: none;
}

.page.active {
  display: block;
}

.intro {
  font-size: 1.15rem;
  color: #475569;
  margin-bottom: 2rem;
  text-align: center;
  line-height: 1.6;
}

.checkbox-group {
  margin-top: 2rem;
  padding: 1.5rem 1.5rem;
  border: 2px dashed #bfdbfe;
  border-radius: 16px;
  background: linear-gradient(135deg, #eff6ff 0%, #dbeafe 100%);
}

.checkbox-group legend {
  font-weight: 700;
  margin-bottom: 1rem;
  color: #1e40af;
  font-size: 1.1rem;
}

.navigation {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-top: 3rem;
  padding-top: 2rem;
  border-top: 1px solid #e5e7eb;
}

.nav-button {
  padding: 0.75rem 1.5rem;
  background: #f3f4f6;
  color: #374151;
  border: 1px solid #d1d5db;
  border-radius: 8px;
  font-weight: 500;
}

.nav-button:hover:not(:disabled) {
  background: #e5e7eb;
  border-color: #9ca3af;
}

.primary {
  background: linear-gradient(120deg, #10b981, #059669);
}

.primary:hover {
  background: linear-gradient(120deg, #059669, #047857);
}

#progress {
  font-weight: 700;
  color: #1f2937;
  font-size: 1.1rem;
}

.status {
  margin-top: 2rem;
  padding: 1.5rem 2rem;
  border-radius: 12px;
  background: #ecfdf5;
  color: #065f46;
  font-weight: 500;
  min-height: 1px;
  border: 1px solid #a7f3d0;
}

.status.status-error {
  background: #fef2f2;
  color: #991b1b;
  border-color: #fca5a5;
}

@media (max-width: 768px) {
  body {
    padding: 1rem;
  }

  form {
    padding: 2rem 1.5rem;
  }

  h1 {
    font-size: 2rem;
  }

  .navigation {
    flex-direction: column;
    gap: 1rem;
  }

  .nav-button {
    width: 100%;
  }
}

const STORAGE_KEY = 'repricing_survey_state_v2';
const form = document.getElementById('surveyForm');
const pages = Array.from(document.querySelectorAll('.page'));
const prevBtn = document.getElementById('prev');
const nextBtn = document.getElementById('next');
const submitBtn = document.getElementById('submitBtn');
const progressEl = document.getElementById('progress');
const statusEl = document.getElementById('status');
const ACTION_URL = form.dataset.action;

let currentPage = 0;
let answers = loadState();
let sending = false;

prefillForm();
updateProgress();
refreshButtons();

prevBtn.addEventListener('click', () => {
  if (currentPage === 0 || sending) return;
  persistPage(currentPage);
  currentPage -= 1;
  showPage(currentPage);
});

nextBtn.addEventListener('click', async () => {
  if (sending) return;
  if (!(await validatePage(currentPage))) return;
  const payload = persistPage(currentPage);
  const isLast = currentPage >= pages.length - 1;
  if (!isLast) {
    await submitProgress(payload, { partial: true });
    currentPage += 1;
    showPage(currentPage);
  } else {
    // last page falls back to full submit
    submitBtn.click();
  }
});

form.addEventListener('submit', async (event) => {
  event.preventDefault();
  if (sending) return;
  if (!(await validatePage(currentPage))) return;
  const payload = persistPage(currentPage);
  await submitProgress(payload, { partial: false });
});

function showPage(index) {
  pages.forEach((page, i) => page.classList.toggle('active', i === index));
  updateProgress();
  refreshButtons();
  window.scrollTo({ top: 0, behavior: 'smooth' });
}

function updateProgress() {
  progressEl.textContent = `${currentPage + 1} / ${pages.length}`;
}

function refreshButtons() {
  prevBtn.disabled = currentPage === 0 || sending;
  const isLast = currentPage === pages.length - 1;
  nextBtn.hidden = isLast;
  submitBtn.hidden = !isLast;
  nextBtn.disabled = sending;
  submitBtn.disabled = sending;
}

async function validatePage(index) {
  const page = pages[index];
  const requiredInputs = Array.from(page.querySelectorAll('[required]'));
  for (const input of requiredInputs) {
    if (!input.value) {
      input.reportValidity();
      input.focus();
      return false;
    }
  }
  return true;
}

function persistPage(index) {
  const data = collectPageData(pages[index]);
  answers = { ...answers, ...data };
  saveState(answers);
  return { ...answers };
}

function collectPageData(page) {
  const data = {};
  const elements = Array.from(page.querySelectorAll('input, select, textarea'));
  const multiMap = {};

  elements.forEach((el) => {
    const name = el.name;
    if (!name) return;

    if (el.type === 'checkbox') {
      if (!multiMap[name]) multiMap[name] = [];
      if (el.checked) multiMap[name].push(el.value);
      data[name] = multiMap[name];
    } else if (el.type === 'radio') {
      if (el.checked) data[name] = el.value;
    } else {
      const value = typeof el.value === 'string' ? el.value.trim() : el.value;
      if (value !== '') data[name] = value;
      else delete data[name];
    }
  });

  return data;
}

async function submitProgress(payload, { partial }) {
  if (!ACTION_URL) {
    announce('No submission endpoint configured.');
    return;
  }

  sending = true;
  refreshButtons();
  announce(partial ? 'Saving progress…' : 'Sending final answers…');

  const submission = {
    ...payload,
    partial,
    page_index: currentPage,
    submitted_at: Date.now(),
  };

  try {
    const response = await fetch(ACTION_URL, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(submission),
    });

    if (!response.ok) {
      throw new Error(`HTTP ${response.status}`);
    }

    const json = await response.json().catch(() => ({}));
    if (!partial) {
      announce('Thanks! Your answers are in. You can close this tab.');
      form.reset();
      answers = {};
      saveState(answers);
      currentPage = 0;
      showPage(currentPage);
    } else {
      announce('Progress saved. Keep going!');
    }
    return json;
  } catch (err) {
    console.warn('Survey submission failed:', err);
    announce('Could not reach the server. Your answers are saved locally—try again soon.', true);
  } finally {
    sending = false;
    refreshButtons();
  }
}

function announce(message, isError = false) {
  if (!statusEl) return;
  statusEl.textContent = message;
  statusEl.classList.toggle('status-error', isError);
}

function saveState(state) {
  try {
    localStorage.setItem(STORAGE_KEY, JSON.stringify(state));
  } catch (_) {
    // ignore storage errors (e.g., private mode)
  }
}

function loadState() {
  try {
    const stored = localStorage.getItem(STORAGE_KEY);
    if (!stored) return {};
    return JSON.parse(stored) || {};
  } catch (_) {
    return {};
  }
}

function prefillForm() {
  if (!answers || typeof answers !== 'object') return;
  const elements = Array.from(form.querySelectorAll('input, select, textarea'));
  elements.forEach((el) => {
    const name = el.name;
    if (!name) return;
    const value = answers[name];
    if (value == null) return;

    if (el.type === 'checkbox') {
      el.checked = Array.isArray(value) ? value.includes(el.value) : Boolean(value);
    } else if (el.type === 'radio') {
      el.checked = el.value === value;
    } else {
      el.value = value;
    }
  });
}
